
public class ShowBits2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int t;
		
		byte val;
		
		val = 123;  // Binary: 0111 1011
		for (t = 128; t > 0; t = t/2) {
			
			if( (val & t) != 0) System.out.print("1 ");
			else System.out.print("0 ");
			
			
			/* 128:
			 * 
			 * 1000 0000  (128)
			 * 0111 1011  (123) &
			 * --------------------
			 * 0000 0000  
			 * 
			 * 64:
			 * 
			 * 0100 0000 (64)
			 * 0111 1011 (123)  &
			 * ---------------------
			 * 0100 0000
			 * 
			 * 32:
			 * 
			 * 0010 0000 (32)
			 * 0111 1011 (123)  & 
			 * ----------------------
			 * 0010 0000  
			 * 
			 * 16: 
			 * 
			 * 0001 0000 (16)
			 * 0111 1011 (123)  &
			 * --------------------
			 * 0001 0000 
			 * 
			 * 8:
			 * 
			 * 0000 1000 (8) 
			 * 0111 1011 (123) & 
			 * -------------------
			 * 0000 1000 
			 * 
			 * 4: 
			 * 
			 * 0000 0100 (4) 
			 * 0111 1011 (123) & 
			 * -------------------
			 * 0000 0000
			 * 
			 * 2:
			 * 
			 * 0000 0010 (2)
			 * 0111 1011 (123)  & 
			 * ----------------------
			 * 0000 0010  
			 * 
			 * 1:
			 * 
			 * 0000 0001 (1) 
			 * 0111 1011 (123) & 
			 * ----------------------
			 * 0000 0001 
			 * 
			 * */
			
		}

	}

}
