package theoretical;

abstract class TwoDShapes{
	
	private double width;
	private double height;
	private String name;
	
	public TwoDShapes() {
		
		width = height = 0.0;
		name = "none";
	}
	
	TwoDShapes(double w, double h, String n){
		width = w; 
		height = h;
		name = n;
	}
}


